import React, {Component} from 'react';
import PropTypes from 'prop-types';
import LoadingSpinner from "../common/LoadingSpinner";
import BackToButton from "../common/BackToButton";
import {ROUTE_MY_PROFILE} from "../auth/ProtectedRoutes";
import {Panel, Button} from "muicss/react";
import OrgLocationPanel from "./OrgLocationPanel";

class Organization extends Component {
    constructor(props) {
        super(props);
        const {fetchOrgDetails, fetchOrgLocations} = props;
        fetchOrgDetails();
        fetchOrgLocations()
    }

    addLocation() {
        //TODO - present add location panel
    }

    render() {
        const {orgDetails, orgLocations} = this.props;

        if (!orgDetails) {
            return <LoadingSpinner/>
        }

        if (!orgDetails.name) {
            return <div className="container">You might not have access to view the details of this organization or it does not exist.</div>
        }
        return (
            <div className="container">
                {/* TODO Add Paper for details and a Paper for each Location*/}
                {/* TODO Button to add location*/}
                <BackToButton route={ROUTE_MY_PROFILE} />
                <Panel>
                    <h1>{orgDetails.name}</h1>
                    <h3>{orgDetails.description}</h3>
                    <h3>Locations:</h3>
                    <hr />
                    {
                        orgLocations
                            ? orgLocations.map(location => <OrgLocationPanel location={location} />)
                            : <div className="center-content"><LoadingSpinner inline={true} /></div>
                    }
                    <div className="center-content">
                        <Button color="primary" onClick={this.addLocation}>
                            <span className="material-icons icon-in-button">add_location</span>Add New Location
                        </Button>
                    </div>
                </Panel>
            </div>
        );
    }
}

Organization.displayName = "Organization";
Organization.propTypes = {
    orgDetails: PropTypes.object,
    orgLocations: PropTypes.object,
    fetchOrgDetails: PropTypes.func.isRequired,
    fetchOrgLocations: PropTypes.func.isRequired
};

export default Organization;
