import React, {Component} from 'react';
import PropTypes from "prop-types";
import LoadingSpinner from "../common/LoadingSpinner";

class Auth0CallbackHandler extends Component {
    componentDidMount() {
        this.handleAuthResponse();
    }

    componentDidUpdate() {
        this.handleAuthResponse();
    }

    handleAuthResponse() {
        const { location, handleAuthentication, setAuth0Response } = this.props;
        if (/access_token|id_token|error/.test(location.hash)) {
            handleAuthentication(setAuth0Response);
        }
    }

    render() {
        return <LoadingSpinner />;
    }
}

Auth0CallbackHandler.displayName = 'Auth0CallbackHandler';

Auth0CallbackHandler.propTypes = {
    handleAuthentication: PropTypes.func.isRequired,
    setAuth0Response: PropTypes.func.isRequired
};

export default Auth0CallbackHandler;
